import React, { useContext, useState } from 'react';
import Modal from './../Modal/Modal';
import { UserContext } from './../context/UserContext';
import './OrderCartModal.css'
import Button from '../UI/Button';

export default function OrderCartModal() {
    const { isOpen, setIsOpen, addItem } = useContext(UserContext);
    const [totalOrderPrice, setTotalOrderPrice] = useState(null)

    let totalAmount = addItem.reduce((acc,curr)=>curr.price+acc,0)
    

    return (
        <Modal>
            <div>
                {
                    addItem.map((item) => {
                        return (
                            <>
                                <div className='orderItem-div'>
                                    <div>
                                        <h3>{item.title}</h3>
                                        <div className='orderItem-price'>
                                            <span>{item.price}</span>
                                            <span>x{item.amount}</span>
                                        </div>


                                    </div>
                                    <div>
                                        <button className='btn1'>-</button>
                                        <button className='btn2'>+</button>
                                    </div>

                                </div>
                                <hr style={{marginLeft:"2rem"}}/>
                            </>
                        )
                    })
                }
            </div>
            <div className='order-main-div'>
                <div className='total-amount-div'>
                    <h3>Total Amount</h3>
                    <span>{totalOrderPrice == null ? "$0.00" : `$${totalOrderPrice}`}</span>
                </div>
                <div className='order-btn'>
                    <Button onClick={() => setIsOpen(!isOpen)}>Close</Button>
                    <Button>Order</Button>
                </div>
            </div>

        </Modal>
    );
}
