import React, { useState,useContext } from 'react'
import './Item.css'
import Button from '../UI/Button'
import { UserContext } from '../context/UserContext'

export default function Item({ title, description, price }) {
  const [amount, setAmount] = useState(1)
  

  const handleChange = (e) => {
    setAmount(Number(e.target.value));
  };

  const handleAdd = () => {
    const total = amount * price;
    console.log(`Title: ${title} | Amount: ${amount} | Total Price: $${total}`);
  };

  return (
    <div className='meal-main-div'>
      <div>
        <h3>{title}</h3>
        <span className='description'>{description}</span>
        <span style={{ display: "block" }} className='meal-price'>${price}</span>
      </div>

      <div className='meal-child-div2'>
        <div>
          <h4>Amount</h4>
          <input
            type="number"
            value={amount}
            min={1}
            onChange={handleChange}
          />
        </div>

        <Button onClick={handleAdd}>+Add</Button>
      </div>

    </div>
  )
}
