import React from 'react';
import ReactDOM from 'react-dom';

const Modal = ({ children }) => {
  return ReactDOM.createPortal(
    <div className="modal-backdrop fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      {children}
        <Cart bg="#fff" shadow="0px 2px 8px" className="bg-white p-6 rounded shadow-lg">
              <h2 className="text-xl font-bold mb-4">This is Modal</h2>
              <button
                onClick={() => setIsOpen(false)}
                className="bg-red-500 text-white px-4 py-2 rounded"
              >
                Close
              </button>
            </Cart>
    </div>,
    document.getElementById('portal-root')
  );
};

export default Modal;
